CREATE VIEW public.report_pos_order AS
 WITH payment_method_by_order_line AS (
         SELECT pol.id AS pos_order_line_id,
            pm_1.pos_order_id,
            (array_agg(pm_1.payment_method_id ORDER BY pm_1.id))[1] AS payment_method_id
           FROM ((public.pos_order_line pol
