--
-- Name: stock_avco_report; Type: VIEW; Schema: public; Owner: odoo_user_name
--

CREATE VIEW public.stock_avco_report AS
 SELECT sm.id,
    sm.product_id,
    sm.date,
    picking.user_id,
    sm.company_id,
    sm.reference,
        CASE
            WHEN sm.is_in THEN sm.value
            ELSE (- sm.value)
        END AS value,
        CASE
            WHEN sm.is_in THEN sm.quantity
            ELSE (- sm.quantity)
        END AS quantity,
    'stock.move'::text AS res_model_name,
    'Operation'::character varying AS description
   FROM (((((public.stock_move sm
     LEFT JOIN public.stock_picking picking ON ((sm.picking_id = picking.id)))
     LEFT JOIN public.product_product pp ON ((sm.product_id = pp.id)))
     LEFT JOIN public.product_template pt ON ((pp.product_tmpl_id = pt.id)))
     LEFT JOIN public.product_category pc ON ((pt.categ_id = pc.id)))
     LEFT JOIN public.res_company company ON ((sm.company_id = company.id)))
  WHERE (((sm.state)::text = 'done'::text) AND ((sm.is_in = true) OR (sm.is_out = true)) AND (((pt.categ_id IS NOT NULL) AND ((pc.property_cost_method ->> (company.id)::text) = ANY (ARRAY['fifo'::text, 'average'::text]))) OR ((pt.categ_id IS NULL) OR ((pc.property_cost_method IS NULL) AND ((company.cost_method)::text = ANY ((ARRAY['fifo'::character varying, 'average'::character varying])::text[]))))))
UNION ALL
 SELECT (- pv.id) AS id,
    pv.product_id,
    pv.date,
    pv.user_id,
    pv.company_id,
    'Adjustment'::character varying AS reference,
    pv.value,
    0 AS quantity,
    'product.value'::text AS res_model_name,
    pv.description
   FROM public.product_value pv
  WHERE (pv.move_id IS NULL);


