-- Name: vendor_delay_report _RETURN; Type: RULE; Schema: public; Owner: odoo_user_name
--

CREATE OR REPLACE VIEW public.vendor_delay_report AS
 SELECT m.id,
    m.date,
    m.purchase_line_id,
    m.product_id,
    min(pc.id) AS category_id,
    min(po.partner_id) AS partner_id,
    min(m.product_qty) AS qty_total,
    sum(
        CASE
            WHEN (((m.state)::text = 'done'::text) AND ((pol.date_planned)::date >= (m.date)::date)) THEN ((ml.quantity * ml_uom.factor) / pt_uom.factor)
            ELSE (0)::numeric
        END) AS qty_on_time
   FROM ((((((((public.stock_move m
     JOIN public.purchase_order_line pol ON ((pol.id = m.purchase_line_id)))
     JOIN public.purchase_order po ON ((po.id = pol.order_id)))
     JOIN public.product_product p ON ((p.id = m.product_id)))
     JOIN public.product_template pt ON ((pt.id = p.product_tmpl_id)))
     JOIN public.uom_uom pt_uom ON ((pt_uom.id = pt.uom_id)))
     LEFT JOIN public.product_category pc ON ((pc.id = pt.categ_id)))
     LEFT JOIN public.stock_move_line ml ON ((ml.move_id = m.id)))
     LEFT JOIN public.uom_uom ml_uom ON ((ml_uom.id = ml.product_uom_id)))
  GROUP BY m.id;


